import React, { useState } from 'react';
import { Container, Row, Col, Card, Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

function CreateListing() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [isRental, setIsRental] = useState(false);
  const [rentalDuration, setRentalDuration] = useState('');
  const [category, setCategory] = useState('');
  const [images, setImages] = useState([]);
  const [video, setVideo] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('price', price);
    formData.append('is_rental', isRental);
    if (isRental) {
      formData.append('rental_duration', rentalDuration);
    }
    formData.append('category', category);

    images.forEach((image, index) => {
      formData.append('images', image, `image${index}`);
    });

    if (video) {
      formData.append('video', video);
    }

    try {
      const response = await axios.post('http://localhost:5000/api/listings', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.message === 'Listing created successfully') {
        navigate('/listings');
      } else {
        setError('Failed to create listing');
      }
    } catch (err) {
      setError('An error occurred while creating the listing');
    }
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImages(files);
  };

  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const categories = [
    'Electronics',
    'Books',
    'Sports Equipment',
    'Clothing',
    'Furniture',
    'Other'
  ];

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={8}>
          <Card className="p-4">
            <Card.Title className="text-center mb-4">
              Create New Listing
            </Card.Title>
            {error && <div className="alert alert-danger">{error}</div>}
            <Form onSubmit={handleSubmit}>
              <Form.Group controlId="formTitle">
                <Form.Label>Title</Form.Label>
                <Form.Control
                  type="text"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group controlId="formDescription" className="mt-3">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group controlId="formPrice" className="mt-3">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  step="0.01"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group controlId="formRental" className="mt-3">
                <Form.Check
                  type="switch"
                  id="rentalSwitch"
                  label="Is this a rental item?"
                  checked={isRental}
                  onChange={(e) => setIsRental(e.target.checked)}
                />
              </Form.Group>

              {isRental && (
                <Form.Group controlId="formRentalDuration" className="mt-3">
                  <Form.Label>Rental Duration (hours)</Form.Label>
                  <Form.Control
                    type="number"
                    value={rentalDuration}
                    onChange={(e) => setRentalDuration(e.target.value)}
                    required
                  />
                </Form.Group>
              )}

              <Form.Group controlId="formCategory" className="mt-3">
                <Form.Label>Category</Form.Label>
                <Form.Select
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  required
                >
                  <option value="">Select category</option>
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>

              <Form.Group controlId="formImages" className="mt-3">
                <Form.Label>Images</Form.Label>
                <Form.Control
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageChange}
                  required
                />
              </Form.Group>

              <Form.Group controlId="formVideo" className="mt-3">
                <Form.Label>Video (optional)</Form.Label>
                <Form.Control
                  type="file"
                  accept="video/*"
                  onChange={handleVideoChange}
                />
              </Form.Group>

              <Button variant="primary" type="submit" className="w-100 mt-3">
                Create Listing
              </Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default CreateListing;
